const INCIDENT_TYPES = ['Alarm', 'Arson', 'Assault', 'Assault with Deadly Weapon',
                        'Breaking & Entering', 'Community Policing', 'Death',
                        'Disorder', 'Drugs', 'Emergency', 'Family Offense', 'Fire',
